import {
  EventBus
} from './event-bus.js';
import GoogleMap from '../components/map'
import itemFormx from '../components/item_form'
import itemDialog from './item_dialog.vue'
import {
  USER_ROLE_GUEST,
  USER_ROLE_REGISTERED,
  ADMIN_ROLE,
  USER_ROLE,
  OWNER_ROLE,

} from "../../constants.js";
export default {
  data: () => ({
    dialog: false,
    switch1: true,
    switch2: false,
    item_id_offer: '',
    radios: 'radio-1',
    submit_name: '',
    item_new_price: '',
    menu_date_from: false,
    menu_date_to: false,
    editdailog: false,
    user_info: {},
    itemId: '',
    token: '',
    map_dialog: false,
    modal2: false,
    offer_info: {


    },
    error: '',
    msg_error_txt: 'هذا اليوم مكرر ',
    urL: 'http://109.224.53.155',

    headers: [{
        text: '#',
        align: 'center',
        sortable: false,
        value: 'id',
      },
      {
        text: 'اسم المركز',
        align: 'center',
        sortable: false,
        value: 'name',
      },


      // { text: 'الوصف', value: 'disc' },
      {
        text: 'سعر الحجز',
        value: 'item_price.price',
        align: 'center',
      },

      {
        text: 'بطاقة دفع',
        value: 'payment',
        sortable: false,
        align: 'center',
      },
      {
        text: 'تاديخ الاضافة',
        value: 'created_at',
        sortable: false,
        align: 'center',
      },

      {
        text: 'العروض',
        value: 'offers',
        sortable: false,
        align: 'center',
      },


      {
        text: ' ',
        value: 'action',
        align: 'center',
      },

    ],
    page: 1,
    pageCount: 0,
    items: [],
    editedIndex: -1,


    valid: false,
    deduction: '',


    img_cliced: -1,
    day_to_open: [],

    nameRules: [
      v => !!v || 'هذا الحقل مطلوب  ',

    ],
    numberRul: [
      v => !!v || 'هذا الحقل مطلوب  ',

      v => !isNaN(v) || 'يجب ادخال رقم ',
    ],
    numberRul1: [

      v => !isNaN(v) || 'يجب ادخال رقم ',
    ],
    descriptRules: [
      v => !!v || 'هذا الحقل مطلوب  ',
      v => v.length >= 20 || 'يجب ان يكون الوصف اكثر من ٢٠ حرف ',
    ],
    loading: false,

    request: 0,
    current_page: 1,
    last_page: 0,
    offer_dialog: false,
    prov_id: '',
    //msg_error:false,
    reserv_type_id: -1,
    offer_url: '',
    reservation_duration: '',
    reservation_duration_foroneday: '',


  }),



  methods: {

    edit_item(item_id) {

      EventBus.$emit('edit_item', item_id);
      this.dialog = true;



    },

    getOfferById(id, old_price) {


      var url = this.urL + "/getByOfferId/" + id;
      axios({
        method: 'get',
        url: url,
        headers: {}

      }).then(response => {
        this.offer_info = response.data.Data;



        this.offer_info.old_price = old_price.price;


      }).catch(error => {
        this.$swal('خطاء', "خطاء بالاتصال", 'error')
      }).finally(d => {


      });


    },

    close_offer() {
      this.offer_dialog = false;
      this.offer_info.new_price = '';
      this.offer_info.old_price = '';
      this.offer_info.discount_percentage = '';


    },

    submit_offer(item_id) {



      if (this.$refs.form.validate()) {



        var data = {

          item_id: item_id,
          offer_name: this.offer_info.offer_name,
          discount_percentage: this.offer_info.discount_percentage,
          offer_disc: this.offer_info.offer_disc,
          offer_from_date: this.offer_info.offer_from_date,
          offer_to_date: this.offer_info.offer_to_date,
          old_price: this.offer_info.old_price,
          is_valid: this.offer_info.is_valid,

        };


        if (this.submit_name == 'edite') {
          this.offer_url = this.urL + '/updateOffer/' + this.offer_info.id;


        } else if (this.submit_name == 'add') {
          this.offer_url = this.urL + '/addOffer';

        } else {

        }




        this.loading = true;
        axios({
            method: 'post',
            url: this.offer_url,
            data: data,
            headers: {
              token: this.token
            }

          })
          .then(response => {
            this.$swal('تم', "    تم اضافة العرض", 'success')
            this.offer_dialog = false;
            this.offer_info.new_price = '';
            this.offer_info.old_price = '';
            this.offer_info.discount_percentage = '';


            this.getitems();
            this.$refs.form.reset();

            if (response.data.success == true) {
              this.$refs.form.reset()
              this.dialog = false;


            }
            // 



          })
          .catch(error => {
            this.err_show = true;
            this.$swal('خطاء', "     لم يتم اضافة الحجز", 'error')

          }).finally(d => {
            this.offer_dialog = false;
            this.loading = false;
          });
      } else {

        this.err_show = true;




      }

    },


    delete_item(id) {



      const Swal = require('sweetalert2')


      Swal.fire({
        title: "هل انت متاكد من الحذف ؟",

        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33',
        confirmButtonText: 'Yes, delete it!'

      }).then((result) => {
        if (result.value) {
          var url = this.urL + "/deletedItem/" + id;
          axios({
            method: 'get',
            url: url,
            headers: {
              token: this.token
            }

          }).then(response => {




          }).catch(error => {
            this.$swal('خطاء', "خطاء بالاتصال", 'error')
          }).finally(d => {
            Swal.fire(
              'تم الحذف بنجاح',
              '',
              'success'
            )
            this.getitems();
          });



        }
      })



    },


    price_percentage(v) {

      this.offer_info.new_price = this.offer_info.old_price;
      this.offer_info.new_price = this.offer_info.old_price - (this.offer_info.old_price * (v / 100));
      this.offer_info.discount_percentage = v;



    },


    select_reser_type(id) {

      this.reserv_type_id = id;
      if (id == 1) {
        this.book_dur_status = true;
        this.book_num_status = false;
      } else if (id == 2) {
        this.book_dur_status = false;
        this.book_num_status = true;
      } else {
        this.book_dur_status = false;
        this.book_num_status = false;
      }
    },

    allowedHours(v) {

      return false;
    },


    edite_offer(id, item_id, old_price) {
      this.submit_name = 'edite';


      this.item_id_offer = item_id;

      this.offer_dialog = true;
      this.getOfferById(id, old_price);



    },

    add_offer(item_id, item_price) {
      this.offer_info = {};

      this.submit_name = 'add';


      this.item_id_offer = item_id;
      this.offer_dialog = true;
      this.offer_info.old_price = item_price.price;



    },

    getitems() {


      this.loading = true

      var url = this.urL + "/getOwnerItems?page=" + this.current_page;
      axios({
        method: 'get',
        url: url,
        headers: {
          token: this.token
        }

      }).then(response => {
        this.items = response.data.Data.data;
        this.last_page = response.data.Data.last_page;
        this.pageCount = response.data.Data.last_page;


      }).catch(error => {
        if (error.response.status == 402) {
          this.$swal(" انتهت الجلسة", "يجب تسجيل الدخول", 'error')
          this.logout();

        } else {
          this.$swal('خطاء', "خطاء بالاتصال", 'error')
        }

      }).finally(d => {
        this.loading = false;
      });

    },




    getMoreitems() {

      if (this.current_page <= this.last_page) {
        this.current_page = this.page;
        this.getitems();
      }

    },
    getCategory() {

      var url = this.urL + "/getInfo/Category";
      axios.get(url).then(response => {
        this.category = response.data.Data;



      })
    },


    showEditdailog(id) {

      this.dialog = true;
      this.itemId = id;

    },


    deleteItem(item) {



      const index = this.items.indexOf(item)
      confirm('تآكيد الغاء الحجز') && axios({
          method: 'post',
          url: this.urL + "/cancelReservation",
          data: {
            reservation_id: item.id
          }
        })
        .then(response => {


          this.getitems()
          // this.items.splice(index, 1)
        })
        .catch(error => {
          this.$swal('خطاء', "خطاء بالاتصال", 'error')
        }) || alert("تم الغاء الامر")





    },

    close() {
      this.dialog = false
      setTimeout(() => {
        this.editedItem = Object.assign({}, this.defaultItem)
        this.editedIndex = -1
      }, 300)
    },

    save() {
      if (this.editedIndex > -1) {
        Object.assign(this.desserts[this.editedIndex], this.editedItem)
      } else {
        this.desserts.push(this.editedItem)
      }
      this.close()
    },

  },

  watch: {
    selected: 'search by sub_cat_id',
  },

  computed: {
    selected() {

      if (this.current_page != this.page) {

        this.getMoreitems();

      }
    }
  },
  mounted() {
    this.token = localStorage.getItem('access_token');
    this.user_info = JSON.parse(localStorage.getItem('user_info'));

    this.getitems();
    this.getCategory();
    this.getCountry();
    this.getProvince();
  },
  components: {

    itemDialog

  }
}
