import Treeselect from '@riophae/vue-treeselect'
// import the styles
import '@riophae/vue-treeselect/dist/vue-treeselect.css'
import cardItem from './sub_components/items_card'
import filterCard from './sub_components/filter'
import func from "../fun"
import {
  EventBus
} from '../event-bus.js';

const axios = require('axios');

export default {
  data() {



    return {
      search: [{
        sub_cat_id: null,

      }],
      id: 0,
      sub_cat: [{
        id: '',
        lable: ''
      }],
      urL: 'http://109.224.53.155',
      items: [],
      getmore: false,
      loading: false,
      value: [],
      current_page: 1,
      last_page: 0,
      options: [],
      xx: null,
      provinces: [],
      categores: [],
      provinces_id: null,
      items_serch_titel: [],
      error_msg: false,
      selected_sub_cat: [],
      options: [],
      optionss: null,
      options_provinces: [],
      orderby: '',
      numof_subcat_selected: -1,
      urlNow: '',
      orderbyselected: '',
      long: 0,
      lat: 0
    }
  },
  mounted() {

    this.getCategory();
    this.Get_All_items_title();
    this.getProvinces();

  },


  methods: {
    setlocation(lat, lng) {
      this.getNearestItems(lat, lng);
    },


    getNearestItems(lat, lng) {
      var url = this.urL + "/getNearestItems/" + lng + "/" + lat;
      this.urlNow = url;
      this.loading = true;
      this.error_msg = false;
      this.getmore = false;
      axios.get(url).then(response => {
        this.items = response.data.Data.data;

        this.current_page = response.data.Data.current_page;
        this.last_page = response.data.Data.last_page;
        //hidden getmore button if there is no more 
        if (this.current_page == this.last_page) {
          this.getmore = false;
        } else {
          this.getmore = true
        }

        if (this.items.length < 1) {
          this.error_msg = true;
        }

      }).catch(error => {

      }).finally(d => {
        this.loading = false
      });
    },

    setCategory(sub_Cat_ides, orderby, lat, long) {
      this.long = long;
      this.lat = lat;
      // if (provinces_id.length > 0) {
      //   this.search.provinces_id = provinces_id;
      // } else {
      //   this.search.provinces_id = null;
      // }


      this.selected_sub_cat = sub_Cat_ides;
      this.search.sub_cat_id = sub_Cat_ides;


      if (this.orderby != orderby) {

        this.orderby = orderby;
        this.search_fun(this.selected_sub_cat, this.provinces_id, null)
      }

      if (this.selected_sub_cat.length != this.numof_subcat_selected && this.selected_sub_cat.length != 0) {

        this.numof_subcat_selected = this.selected_sub_cat.length;
        this.search.sub_cat_id = this.selected_sub_cat;
        this.search.text_titel = null;



        this.search_fun(this.selected_sub_cat, this.provinces_id, null)
      } else if (this.selected_sub_cat.length == 0 && this.numof_subcat_selected != -1) {

        this.numof_subcat_selected = -1;

        this.search_fun_start();


      }
    },


    getCategory() {

      var url = this.urL + "/getInfo/Category";
      axios.get(url).then(response => {
        this.categores = response.data.Data;
        var i = 0;
        for (i = 0; i <= this.categores.length; i++) {
          this.categores[i].id = i + 100;





          this.sub_cat = this.categores[i].sub_category.map(item => {
            return {
              id: item.id,
              label: item.name
            };
          });


          this.options.push({
              id: 'x' + this.categores[i].id,
              label: this.categores[i].name,
              children: this.sub_cat




            }

          );



        }


      }).finally(s => {



      });


    },



    Get_All_items_title() {

      var url = this.urL + "/getLast?page=1";
      axios.get(url).then(response => {
        var items_serch = response.data.Data.data;

        var i = 0;

        for (i = 0; i < items_serch.length; i++) {

          this.items_serch_titel.push(items_serch[i].name);

        }


      }).catch(error => {

      });


    },



    filter(input) {

      if (input.length < 1) {
        this.search.text_titel = null;
        return []
      }
      return this.items_serch_titel.filter(item => {

        //save the return text search
        this.search.text_titel = input;


        return item.toLowerCase()
          .startsWith(input.toLowerCase())
      })
    },


    search_select() {

      //Seach  ThE  Selected Autocomplent titel
      //  this.search_fun(this.search.sub_cat_id, null, input_search);

    },


    search_fun(sub_Cat_id, provinces_id, title) {

      this.xx = title;
      this.getmore = false;
      this.error_msg = false
      this.loading = true



      if (!this.xx) {
        this.xx = null;

      } else {
        this.xx = title;
      }




      if (sub_Cat_id == undefined) {
        sub_Cat_id = undefined;
      } else if (sub_Cat_id.length == 0) {
        sub_Cat_id = undefined
      } else {
        sub_Cat_id = sub_Cat_id;
      }





      if (provinces_id == undefined) {
        provinces_id = null;
      }
      if (isNaN(this.xx)) {
        this.xx = null;

      } else {

      }


      var header;
      let self = this;



      func.chekpermition().then(function (result) {
        // Will return ['granted', 'prompt', 'denied']




        if (result.state == "granted" && self.orderby == "nearest") {

          header = {
            long: self.long,
            lat: self.lat
          };
        } else if (result.state == "denied" && self.orderby == "nearest") {
          header = {};

          self.error_msg = true
          self.loading = false
          return;
          self.$swal("", "   يجب تفعيل الوصول للموقع", 'warning')
        } else if (result.state == "prompt" && self.orderby == "nearest") {

          self.error_msg = true
          self.loading = false
          return;
          header = {};
          self.$swal("", "   فعل الوصول للموقع رجاءاً   ", 'warning')
        } else {
          header = {};

        }






        axios({
            method: 'get',
            url: self.urL + "/search/" + sub_Cat_id + "/" + provinces_id + "/" + title + "/" + self.orderby +
              "?page=" + self.current_page,

            headers: header
          })
          .then(response => {

            self.error_msg = false
            self.items = response.data.Data.data;

            self.xx = null;
            self.current_page = response.data.Data.current_page;
            self.last_page = response.data.Data.last_page;

            //hidden getmore button if there is no more 
            if (self.current_page == self.last_page) {
              self.getmore = false;
            } else {
              self.getmore = true
            }
            if (self.items.length < 1) {
              self.error_msg = true
            }


          })
          .catch(
            self.error_msg = true

          ).finally(
            self.loading = false
          );
      });

    },
    getProvinces() {


      var url = this.urL + "/getInfo/Province/1";
      axios.get(url).then(response => {
        this.provinces = response.data.Data;
        var i = 0;
        for (i = 0; i <= this.provinces.length; i++) {

          this.options_provinces.push({
            id: this.provinces[i].id,
            label: this.provinces[i].province_name,
          });


        }


      });

    },

    getdays(date) {

      var days = ["السبت", "الاحد", "الاثنين", "الثلاثاء", "الاربعاء", "الخميس", "الجمعة"];
      if (date.every_day == 0) {
        if (days[date.from_day] == days[date.to_day]) {
          return days[date.from_day]
        } else {
          return days[date.from_day] + " - " + days[date.to_day];
        }


      } else {
        return "كل الايام";
      }
    },


    getMore_page() {
      this.loading = true;
      if (this.current_page == this.last_page) {
        this.getmore = false;
        this.loading = false;
      } else {


        this.current_page = this.current_page + 1;
        //   this.search_fun(this.search.sub_cat_id, this.search.provinces_id, this.search.text_titel)




      }

    },



  },
  created() {

    this.orderby = this.$route.params.orderby;
    this.orderbyselected = this.orderby;
    if (this.$route.params.cat_id != "null" && this.$route.params.cat_id != "undefined") {
      var sub_ides = this.$route.params.cat_id.split(",");

      for (var i = 0; i < sub_ides.length; i++) {
        var sub_Cat_id = parseInt(sub_ides[i], 10);

        this.selected_sub_cat.push(sub_Cat_id);
      }


      var provns = this.$route.params.gov_id.split(",");
      var provinces_ides = [];
      for (var ii = 0; ii < provns.length; ii++) {




        provinces_ides.push(provns[ii]);

      }

      if (provinces_ides[0] != "null") {
        this.provinces_id = provinces_ides;
      }



      //save the lenght of  sup cat selected
      this.numof_subcat_selected = this.selected_sub_cat.length;

      this.search.sub_cat_id = this.selected_sub_cat;

    }
    this.search_fun(this.$route.params.cat_id, this.$route.params.gov_id, this.$route.params.tit);

    EventBus.$on('searchx', items => {
      this.items = items;




    });

  },
  computed: {

    selected() {



    },
  },
  watch: {
    selected: 'search by sub_cat_id',
  },
  components: {
    Treeselect,
    cardItem,
    filterCard


  }
};
